mapper.update:
UPDATE POST_BOARD
SET POST_VIEWS = POST_VIEWS + 1
WHERE POST_NO = #{postNo}

UPDATE POST_BOARD
SET POST_TEXT = #{postText}
WHERE POST_NO = #{postNo}

mapper.select:
SELECT COUNT(1) AS CNT
FROM POST_BOARD
WHERE ORIGIN_NO = #{postNo}

SELECT POST_NO, POST_TITLE, POST_TEXT, POST_INSERT, POST_INSDT, ORIGIN_NO, GROUP_ORD
FROM POST_BOARD
WHERE ORIGIN_NO = #{postNo}

SELECT POST_NO, POST_TITLE, POST_TEXT, POST_INSERT, POST_INSDT, ORIGIN_NO, GROUP_ORD
FROM POST_BOARD
WHERE POST_NO = #{postNo}
